import React, { useState, useEffect } from 'react';
import { useSearchParams } from 'react-router-dom';
import { searchWikipedia, type SearchResult as WikiSearchResult } from '../api/wikipedia';
import { addToSearchHistory } from '../services/historyService';
import SearchBar from '../components/SearchBar';
import FunFacts from '../components/FunFacts';
import './SearchPage.css';

const SearchPage: React.FC = () => {
  const [searchParams] = useSearchParams();
  const [results, setResults] = useState<WikiSearchResult[]>([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const query = searchParams.get('q');
    if (query) {
      setLoading(true);
      searchWikipedia(query).then(searchResults => {
        setResults(searchResults);
        setLoading(false);
        // Track the search
        addToSearchHistory(query);
      });
    } else {
      setResults([]);
    }
  }, [searchParams]);

  const query = searchParams.get('q');

  if (!query) {
    return (
      <div className="search-page">
        <SearchBar />
        <FunFacts />
      </div>
    );
  }

  return (
    <div className="search-page">
      <SearchBar />
      {loading ? (
        <div className="search-loading">Loading results...</div>
      ) : results.length > 0 ? (
        <div className="search-results">
          {results.map((result) => (
            <SearchResult key={result.pageid} result={result} />
          ))}
        </div>
      ) : (
        <div className="no-results">
          <h2>No results found</h2>
          <p>Try different keywords or check your spelling</p>
        </div>
      )}
    </div>
  );
};

interface SearchResultProps {
  result: WikiSearchResult;
}

const SearchResult: React.FC<SearchResultProps> = ({ result }) => {
  return (
    <a 
      href={`/article/${encodeURIComponent(result.title)}`} 
      className="search-result"
    >
      {result.images && result.images.length > 0 && (
        <div className="result-image">
          <img 
            src={result.images[0].url} 
            alt={result.images[0].description || result.title}
            loading="lazy"
          />
        </div>
      )}
      <div className="result-content">
        <h3>{result.title}</h3>
        <p dangerouslySetInnerHTML={{ __html: result.snippet }} />
      </div>
    </a>
  );
};

export default SearchPage;
