import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useHistory } from '../hooks/useHistory';
import { formatDate, formatTime } from '../utils/dateUtils';
import '../styles/shared.css';
import './HistoryPage.css';

const HistoryPage: React.FC = () => {
  const { groupedHistory } = useHistory();
  const navigate = useNavigate();

  if (Object.keys(groupedHistory).length === 0) {
    return (
      <div className="page-container">
        <h1 className="page-title">Activity</h1>
        <div className="history-empty">
          <h2>No activity yet</h2>
          <p>Articles you view will appear here</p>
        </div>
      </div>
    );
  }

  return (
    <div className="page-container">
      <h1 className="page-title">Activity</h1>
      <div className="history-list">
        {Object.entries(groupedHistory).map(([date, articles]) => (
          <div key={date} className="history-group">
            <h2 className="history-date-header">
              {formatDate(new Date(date))}
            </h2>
            {articles.map((article) => (
              <div
                key={`${article.pageid}-${article.timestamp}`}
                className="history-item"
                onClick={() => navigate(`/article/${article.pageid}`)}
              >
                {article.thumbnail && (
                  <div className="history-thumbnail">
                    <img
                      src={article.thumbnail}
                      alt={article.title}
                      loading="lazy"
                    />
                  </div>
                )}
                <div className="history-item-content">
                  <h3>{article.title}</h3>
                  <p className="history-snippet">{article.snippet}</p>
                  <div className="history-date">
                    {formatTime(new Date(article.timestamp))}
                  </div>
                </div>
              </div>
            ))}
          </div>
        ))}
      </div>
    </div>
  );
};

export default HistoryPage;
