import React from 'react';
import { useNavigate } from 'react-router-dom';
import './FunFacts.css';

interface FunFact {
  title: string;
  description: string;
  link: string;
  imageUrl?: string;
}

const FunFacts: React.FC = () => {
  const navigate = useNavigate();

  const funFacts: FunFact[] = [
    {
      title: "Argo - Pompeii's Guardian Dog",
      description: "A stray dog named Argo visited the ruins of Pompeii daily for 15 years and was considered its 'guardian'",
      link: "Argo_(Pompeii_dog)",
      imageUrl: "https://upload.wikimedia.org/wikipedia/commons/thumb/1/1f/Pompeii_-_Casa_del_Poeta_Tragico_-_MAN.jpg/640px-Pompeii_-_Casa_del_Poeta_Tragico_-_MAN.jpg"
    },
    {
      title: "Kentuck Knob",
      description: "The second owner of Kentuck Knob, a house by Frank Lloyd Wright, 'fell in love with the outside' and wanted to see the inside so urgently that he decided to purchase it",
      link: "Kentuck_Knob",
      imageUrl: "https://upload.wikimedia.org/wikipedia/commons/thumb/1/17/Kentuck_Knob_PA1.jpg/640px-Kentuck_Knob_PA1.jpg"
    }
  ];

  const handleFactClick = (link: string) => {
    navigate(`/article/${link}`);
  };

  return (
    <div className="fun-facts">
      <h2 className="fun-facts-title">Did you know?</h2>
      <div className="fun-facts-grid">
        {funFacts.map((fact, index) => (
          <div
            key={index}
            className="fun-fact-card"
            onClick={() => handleFactClick(fact.link)}
          >
            {fact.imageUrl && (
              <div className="fun-fact-image">
                <img src={fact.imageUrl} alt={fact.title} loading="lazy" />
              </div>
            )}
            <div className="fun-fact-content">
              <h3>{fact.title}</h3>
              <p>{fact.description}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default FunFacts;
