import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import SearchBar from '../components/SearchBar';
import { searchWikipedia, SearchResult } from '../api/wikipedia';
import './SearchPage.css';

const SearchPage: React.FC = () => {
  const [results, setResults] = useState<SearchResult[]>([]);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleSearch = async (query: string) => {
    setLoading(true);
    const searchResults = await searchWikipedia(query);
    setResults(searchResults);
    setLoading(false);
  };

  const handleResultClick = (title: string) => {
    navigate(`/article/${encodeURIComponent(title)}`);
  };

  return (
    <div className="search-page">
      <SearchBar onSearch={handleSearch} />
      <div className="search-results">
        {loading ? (
          <div className="loading">Searching...</div>
        ) : (
          results.map((result) => (
            <div
              key={result.pageid}
              className="search-result"
              onClick={() => handleResultClick(result.title)}
            >
              <div className="result-content">
                <h3>{result.title}</h3>
                <div dangerouslySetInnerHTML={{ __html: result.snippet }} />
              </div>
              {result.images && result.images.length > 0 && (
                <div className="result-images">
                  {result.images.map((image, index) => (
                    <div key={index} className="image-container">
                      <img
                        src={image.url}
                        alt={image.description || result.title}
                        loading="lazy"
                      />
                    </div>
                  ))}
                </div>
              )}
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default SearchPage;
