import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { getArticleContent, getArticleImages, ArticleImage } from '../api/wikipedia';
import { useHistory } from '../hooks/useHistory';
import '../styles/shared.css';
import './ArticlePage.css';

const ArticlePage: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const [content, setContent] = useState<string>('');
  const [images, setImages] = useState<ArticleImage[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [title, setTitle] = useState<string>('');
  const { addToHistory } = useHistory();

  useEffect(() => {
    const fetchArticle = async () => {
      if (!id) return;

      setLoading(true);
      setError(null);

      try {
        // First get the title from the page ID
        const titleParams = new URLSearchParams({
          action: 'query',
          pageids: id,
          format: 'json',
          origin: '*'
        });

        const titleResponse = await fetch(`https://en.wikipedia.org/w/api.php?${titleParams}`);
        const titleData = await titleResponse.json();
        const page = titleData.query.pages[id];
        const pageTitle = page.title;
        setTitle(pageTitle);

        // Then fetch content and images
        const [articleContent, articleImages] = await Promise.all([
          getArticleContent(pageTitle),
          getArticleImages(pageTitle)
        ]);

        setContent(articleContent);
        setImages(articleImages);

        // Add to history
        addToHistory({
          pageid: parseInt(id),
          title: pageTitle,
          snippet: articleContent.split('\n')[0],
          thumbnail: articleImages[0]?.url,
          timestamp: Date.now()
        });
      } catch (err) {
        setError('Failed to load article');
        console.error('Error loading article:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchArticle();
  }, [id, addToHistory]);

  if (loading) {
    return (
      <div className="page-container">
        <div className="article-loading">Loading article...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="page-container">
        <div className="article-error">{error}</div>
      </div>
    );
  }

  const heroImage = images[0];
  const remainingImages = images.slice(1);

  return (
    <div className="article-page">
      {heroImage ? (
        <div className="article-hero">
          <div className="hero-image-container">
            <img
              src={heroImage.url}
              alt={heroImage.description || 'Article hero image'}
              loading="eager"
            />
            <div className="hero-overlay">
              <h1 className="article-title">{title}</h1>
            </div>
          </div>
        </div>
      ) : (
        <div className="article-hero no-image">
          <h1 className="article-title">{title}</h1>
        </div>
      )}

      <article className="article-content">
        {remainingImages.length > 0 && (
          <div className="article-images">
            {remainingImages.map((image, index) => (
              <div key={index} className="article-image-container">
                <img
                  src={image.url}
                  alt={image.description || 'Article image'}
                  loading="lazy"
                />
                {image.description && (
                  <div className="image-caption">{image.description}</div>
                )}
              </div>
            ))}
          </div>
        )}

        <div className="article-text">
          {content.split('\n').map((paragraph, index) => (
            paragraph.trim() ? (
              <p key={index}>{paragraph}</p>
            ) : null
          ))}
        </div>
      </article>
    </div>
  );
};

export default ArticlePage;
