import React from 'react';
import { useNavigate } from 'react-router-dom';
import ImageCarousel from './ImageCarousel';
import './FunFacts.css';

interface FunFact {
  text: string;
  relatedArticles: {
    title: string;
    description: string;
    link: string;
    imageUrl?: string;
  }[];
}

const FunFacts: React.FC = () => {
  const navigate = useNavigate();

  const funFacts: FunFact[] = [
    {
      text: "Did you know... that a stray dog named Argo visited the ruins of Pompeii daily for 15 years and was considered its 'guardian'?",
      relatedArticles: [
        {
          title: "Argo - Pompeii's Guardian Dog",
          description: "The heartwarming story of a stray dog who became the unofficial guardian of the ancient ruins of Pompeii",
          link: "Argo_(Pompeii_dog)",
          imageUrl: "https://upload.wikimedia.org/wikipedia/commons/thumb/1/1f/Pompeii_-_Casa_del_Poeta_Tragico_-_MAN.jpg/640px-Pompeii_-_Casa_del_Poeta_Tragico_-_MAN.jpg"
        },
        {
          title: "Pompeii",
          description: "The ancient Roman city preserved under volcanic ash from the eruption of Mount Vesuvius in 79 AD",
          link: "Pompeii",
          imageUrl: "https://upload.wikimedia.org/wikipedia/commons/thumb/6/6d/Pompeji_Forum.JPG/640px-Pompeji_Forum.JPG"
        }
      ]
    },
    {
      text: "Did you know... that the second owner of Kentuck Knob, a house by Frank Lloyd Wright, 'fell in love with the outside' and wanted to see the inside so urgently that he decided to purchase it?",
      relatedArticles: [
        {
          title: "Kentuck Knob",
          description: "A house designed by renowned architect Frank Lloyd Wright, featuring his signature organic architecture style",
          link: "Kentuck_Knob",
          imageUrl: "https://upload.wikimedia.org/wikipedia/commons/thumb/1/17/Kentuck_Knob_PA1.jpg/640px-Kentuck_Knob_PA1.jpg"
        },
        {
          title: "Frank Lloyd Wright",
          description: "The legendary American architect known for his organic architecture and innovative designs",
          link: "Frank_Lloyd_Wright",
          imageUrl: "https://upload.wikimedia.org/wikipedia/commons/thumb/3/3f/Frank_Lloyd_Wright_portrait.jpg/640px-Frank_Lloyd_Wright_portrait.jpg"
        }
      ]
    }
  ];

  const handleArticleClick = (link: string) => {
    navigate(`/article/${link}`);
  };

  return (
    <div className="fun-facts">
      <h2 className="fun-facts-title">Did you know?</h2>
      <div className="fun-facts-list">
        {funFacts.map((fact, index) => (
          <div key={index} className="fun-fact-section">
            <p className="fun-fact-text">{fact.text}</p>
            <div className="related-articles">
              <h3 className="related-articles-title">Related Articles</h3>
              <div className="articles-grid">
                {fact.relatedArticles.map((article, articleIndex) => (
                  <div
                    key={articleIndex}
                    className="article-card"
                    onClick={() => handleArticleClick(article.link)}
                  >
                    <ImageCarousel
                      images={[
                        {
                          url: article.imageUrl || '',
                          alt: article.title
                        }
                      ]}
                    />
                    <div className="article-content">
                      <h4>{article.title}</h4>
                      <p>{article.description}</p>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default FunFacts;
