import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getArticleImages, getTodaysDidYouKnow, ArticleImage } from '../api/wikipedia';
import './FunFacts.css';

const FunFacts: React.FC = () => {
  const navigate = useNavigate();
  const [facts, setFacts] = useState<any[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchFacts = async () => {
      try {
        setIsLoading(true);
        setError(null);
        const todaysFacts = await getTodaysDidYouKnow();

        const factsWithImages = await Promise.all(
          todaysFacts.map(async (fact) => {
            const articlesWithImages = await Promise.all(
              fact.relatedArticles.map(async (article) => {
                const images = await getArticleImages(article.link);
                return {
                  ...article,
                  thumbnail: images.length > 0 ? images[0] : undefined
                };
              })
            );
            return {
              ...fact,
              relatedArticles: articlesWithImages
            };
          })
        );
        setFacts(factsWithImages);
      } catch (err) {
        setError('Failed to load today\'s facts. Please try again later.');
        console.error('Error fetching facts:', err);
      } finally {
        setIsLoading(false);
      }
    };

    fetchFacts();
  }, []);

  const handleArticleClick = (link: string) => {
    navigate(`/article/${encodeURIComponent(link)}`);
  };

  if (isLoading) {
    return (
      <div className="fun-facts-container loading">
        <div className="loading-spinner">Loading today's interesting facts...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="fun-facts-container error">
        <div className="error-message">{error}</div>
      </div>
    );
  }

  return (
    <div className="fun-facts-container">
      {facts.map((fact, index) => (
        <div key={index} className="fun-fact">
          <p className="fact-text">{fact.text}</p>
          <div className="related-articles">
            {fact.relatedArticles.map((article: any, articleIndex: number) => (
              <div
                key={articleIndex}
                className={`article-card ${!article.thumbnail ? 'no-image' : ''}`}
                onClick={() => handleArticleClick(article.link)}
              >
                {article.thumbnail && article.thumbnail.url && (
                  <div className="article-thumbnail">
                    <img
                      src={article.thumbnail.url}
                      alt={article.thumbnail.description || article.title}
                      loading="lazy"
                    />
                  </div>
                )}
                <div className="article-content">
                  <h3>{decodeURIComponent(article.link.replace(/_/g, ' '))}</h3>
                  <p>{article.description}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default FunFacts;
