import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getHistory, HistoryItem } from '../services/historyService';
import './HistoryPage.css';

const HistoryPage: React.FC = () => {
  const [history, setHistory] = useState<HistoryItem[]>([]);
  const navigate = useNavigate();

  useEffect(() => {
    setHistory(getHistory());
  }, []);

  const formatDate = (timestamp: number) => {
    const date = new Date(timestamp);
    return date.toLocaleDateString(undefined, {
      month: 'short',
      day: 'numeric',
      year: 'numeric',
    });
  };

  const handleArticleClick = (title: string) => {
    navigate(`/article/${encodeURIComponent(title)}`);
  };

  if (history.length === 0) {
    return (
      <div className="history-page">
        <div className="history-empty">
          <h2>No History</h2>
          <p>Articles you view will appear here</p>
        </div>
      </div>
    );
  }

  return (
    <div className="history-page">
      <div className="history-list">
        {history.map((item, index) => (
          <div
            key={`${item.title}-${item.timestamp}`}
            className="history-item"
            onClick={() => handleArticleClick(item.title)}
          >
            <div className="history-item-content">
              <h3>{decodeURIComponent(item.title)}</h3>
              <p className="history-snippet">{item.snippet}</p>
              <span className="history-date">{formatDate(item.timestamp)}</span>
            </div>
            {item.thumbnail && (
              <div className="history-thumbnail">
                <img
                  src={item.thumbnail.url}
                  alt={item.thumbnail.description || item.title}
                  loading="lazy"
                />
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default HistoryPage;
