import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getArticleImages, ArticleImage, getDidYouKnowFacts } from '../api/wikipedia';
import './FunFacts.css';

interface FunFact {
  text: string;
  relatedArticles: {
    title: string;
    description: string;
    link: string;
    thumbnail?: ArticleImage;
  }[];
}

const FunFacts: React.FC = () => {
  const navigate = useNavigate();
  const [facts, setFacts] = useState<FunFact[]>([]);

  useEffect(() => {
    const fetchFacts = async () => {
      try {
        // Fetch Did You Know facts from Wikipedia
        const dykFacts = await getDidYouKnowFacts();

        // Transform and enhance the facts with images
        const enhancedFacts = await Promise.all(
          dykFacts.map(async (fact) => {
            const articlesWithImages = await Promise.all(
              fact.relatedArticles.map(async (article) => {
                const images = await getArticleImages(article.link);
                return {
                  ...article,
                  description: `Article from Wikipedia's Did You Know section`,
                  thumbnail: images.length > 0 ? images[0] : undefined
                };
              })
            );
            return {
              text: fact.text,
              relatedArticles: articlesWithImages
            };
          })
        );

        setFacts(enhancedFacts);
      } catch (error) {
        console.error('Error fetching facts:', error);
      }
    };

    fetchFacts();
  }, []);

  const handleArticleClick = (link: string) => {
    navigate(`/article/${encodeURIComponent(link)}`);
  };

  return (
    <div className="fun-facts-container">
      {facts.map((fact, index) => (
        <div key={index} className="fun-fact">
          <p className="fact-text">{fact.text}</p>
          <div className="related-articles">
            {fact.relatedArticles.map((article, articleIndex) => (
              <div
                key={articleIndex}
                className={`article-card ${!article.thumbnail ? 'no-image' : ''}`}
                onClick={() => handleArticleClick(article.link)}
              >
                {article.thumbnail && article.thumbnail.url && (
                  <div className="article-thumbnail">
                    <img
                      src={article.thumbnail.url}
                      alt={article.thumbnail.description || article.title}
                      loading="lazy"
                    />
                  </div>
                )}
                <div className="article-content">
                  <h3>{decodeURIComponent(article.link.replace(/_/g, ' '))}</h3>
                  <p>{article.description}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default FunFacts;
