import React, { useState, useEffect } from 'react';
import { useSearchParams, useNavigate } from 'react-router-dom';
import { searchWikipedia, SearchResult } from '../api/wikipedia';
import { getHistory } from '../services/historyService';
import SearchBar from '../components/SearchBar';
import './SearchPage.css';

const FEATURED_ARTICLES = [
  {
    title: "Dracunculiasis",
    snippet: "Also called Guinea-worm disease, is a parasitic infection by the Guinea worm. A disease of extreme poverty, there were 14 cases reported worldwide in 2023, as efforts continue to eradicate it.",
  },
  {
    title: "Red-capped_parrot",
    snippet: "A species of broad-tailed parrot native to southwestern Australia. The species general plumage is green with a red crown and yellowish-green cheeks.",
  },
  {
    title: "Cher",
    snippet: "American singer, actress and television personality. Often referred to as the 'Goddess of Pop', she is noted for her distinctive contralto singing voice and for having worked in numerous areas of entertainment.",
  },
  {
    title: "Malcolm_X",
    snippet: "American Muslim minister and human rights activist who was a prominent figure during the civil rights movement. A spokesman for the Nation of Islam until 1964, he was a vocal advocate for Black empowerment.",
  }
];

const DID_YOU_KNOW = [
  {
    title: "BergebylÃ¸pet_N70",
    snippet: "A long-distance sled dog race with a route entirely north of the 70th parallel north.",
  },
  {
    title: "Kentuck_Knob",
    snippet: "A house by Frank Lloyd Wright whose second owner 'fell in love with the outside' and wanted to see the inside so urgently that he decided to purchase it.",
  },
  {
    title: "Argo_(Pompeii_dog)",
    snippet: "A stray dog that visited the ruins of Pompeii daily for 15 years and was considered its 'guardian'.",
  }
];

const SearchPage: React.FC = () => {
  const [searchParams] = useSearchParams();
  const [results, setResults] = useState<SearchResult[]>([]);
  const [loading, setLoading] = useState(false);
  const [recentArticles, setRecentArticles] = useState<any[]>([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Get recent articles from history
    const history = getHistory();
    setRecentArticles(history.slice(0, 3)); // Show only 3 most recent

    const query = searchParams.get('q');
    if (query) {
      handleSearch(query);
    }
  }, [searchParams]);

  const handleSearch = async (query: string) => {
    setLoading(true);
    try {
      const searchResults = await searchWikipedia(query);
      setResults(searchResults);
    } catch (error) {
      console.error('Error searching:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleArticleClick = (title: string) => {
    navigate(`/article/${encodeURIComponent(title)}`);
  };

  return (
    <div className="search-page">
      <h1 className="search-title">Search</h1>
      <SearchBar />

      {!searchParams.get('q') && (
        <div className="suggestions-container">
          {recentArticles.length > 0 && (
            <section className="suggestion-section">
              <h2>Continue Reading</h2>
              <div className="suggestion-grid">
                {recentArticles.map((article, index) => (
                  <div 
                    key={`${article.title}-${index}`}
                    className="suggestion-card"
                    onClick={() => handleArticleClick(article.title)}
                  >
                    {article.thumbnail && (
                      <div className="suggestion-image">
                        <img 
                          src={article.thumbnail.url} 
                          alt={article.title}
                          loading="lazy"
                        />
                      </div>
                    )}
                    <div className="suggestion-content">
                      <h3>{decodeURIComponent(article.title)}</h3>
                      <p>{article.snippet}</p>
                    </div>
                  </div>
                ))}
              </div>
            </section>
          )}

          <section className="suggestion-section">
            <h2>Featured Articles</h2>
            <div className="suggestion-grid">
              {FEATURED_ARTICLES.map((article, index) => (
                <div 
                  key={index}
                  className="suggestion-card"
                  onClick={() => handleArticleClick(article.title)}
                >
                  <div className="suggestion-content">
                    <h3>{article.title.replace(/_/g, ' ')}</h3>
                    <p>{article.snippet}</p>
                  </div>
                </div>
              ))}
            </div>
          </section>

          <section className="suggestion-section">
            <h2>Did You Know?</h2>
            <div className="suggestion-grid">
              {DID_YOU_KNOW.map((article, index) => (
                <div 
                  key={index}
                  className="suggestion-card"
                  onClick={() => handleArticleClick(article.title)}
                >
                  <div className="suggestion-content">
                    <h3>{article.title.replace(/_/g, ' ')}</h3>
                    <p>{article.snippet}</p>
                  </div>
                </div>
              ))}
            </div>
          </section>
        </div>
      )}

      <div className="search-results">
        {loading ? (
          <div className="loading">Searching...</div>
        ) : results.length > 0 ? (
          results.map((result) => (
            <div 
              key={result.pageid} 
              className="search-result"
              onClick={() => handleArticleClick(result.title)}
            >
              <h2>{result.title}</h2>
              <div 
                className="snippet"
                dangerouslySetInnerHTML={{ __html: result.snippet }}
              />
              {result.images && result.images.length > 0 && (
                <div className="result-images">
                  {result.images.map((image, index) => (
                    <div key={index} className="image-container">
                      <img
                        src={image.url}
                        alt={image.description || result.title}
                        loading="lazy"
                      />
                    </div>
                  ))}
                </div>
              )}
            </div>
          ))
        ) : searchParams.get('q') ? (
          <div className="no-results">No results found</div>
        ) : null}
      </div>
    </div>
  );
};

export default SearchPage;
