import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getArticleContent, getArticleImages, ArticleImage } from '../api/wikipedia';
import { addToHistory } from '../services/historyService';
import './ArticlePage.css';

const ArticlePage: React.FC = () => {
  const { title } = useParams<{ title: string }>();
  const navigate = useNavigate();
  const [content, setContent] = useState<string>('');
  const [heroImage, setHeroImage] = useState<ArticleImage | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchArticle = async () => {
      if (!title) return;

      setLoading(true);
      setError(null);

      try {
        const [articleContent, articleImages] = await Promise.all([
          getArticleContent(title),
          getArticleImages(title)
        ]);

        // Process the HTML content to handle internal links
        const processedContent = processWikipediaContent(articleContent);
        setContent(processedContent);

        if (articleImages.length > 0) {
          setHeroImage(articleImages[0]);
        }

        // Add to history when successfully loaded
        const tempDiv = document.createElement('div');
        tempDiv.innerHTML = articleContent;
        const firstParagraph = tempDiv.querySelector('p')?.textContent || '';
        addToHistory(
          title,
          firstParagraph,
          articleImages[0]
        );
      } catch (err) {
        setError('Failed to load article');
        console.error('Error loading article:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchArticle();
  }, [title]);

  const processWikipediaContent = (htmlContent: string): string => {
    const tempDiv = document.createElement('div');
    tempDiv.innerHTML = htmlContent;

    // Process internal links
    tempDiv.querySelectorAll('a').forEach(link => {
      const href = link.getAttribute('href');
      if (href?.startsWith('/wiki/')) {
        const articleTitle = href.replace('/wiki/', '');
        link.setAttribute('data-article', articleTitle);
        link.removeAttribute('href');
        link.classList.add('wiki-link');
      }
    });

    // Process sections to make them collapsible
    let sectionId = 0;
    tempDiv.querySelectorAll('h2').forEach(heading => {
      // Create section wrapper
      const section = document.createElement('div');
      section.className = 'wiki-section';

      // Create header wrapper with toggle button
      const header = document.createElement('div');
      header.className = 'wiki-section-header';

      // Add toggle button
      const toggle = document.createElement('button');
      toggle.className = 'section-toggle';
      toggle.setAttribute('aria-expanded', 'false');
      toggle.innerHTML = '<svg viewBox="0 0 24 24" class="section-toggle-icon"><path d="M7 10l5 5 5-5z"/></svg>';

      // Move the heading into the header
      header.appendChild(toggle);
      header.appendChild(heading.cloneNode(true));

      // Create content wrapper
      const content = document.createElement('div');
      content.className = 'wiki-section-content collapsed';
      content.id = `section-${sectionId}`;

      // Move all content until the next h2 into the content div
      let nextEl = heading.nextElementSibling;
      while (nextEl && nextEl.tagName !== 'H2') {
        const temp = nextEl.nextElementSibling;
        content.appendChild(nextEl);
        nextEl = temp;
      }

      // Assemble the section
      section.appendChild(header);
      section.appendChild(content);

      // Replace the original heading with the new section
      heading.parentNode?.replaceChild(section, heading);
      sectionId++;
    });

    // Remove edit links and other Wikipedia UI elements
    tempDiv.querySelectorAll('.mw-editsection').forEach(el => el.remove());

    return tempDiv.innerHTML;
  };

  const handleWikiLinkClick = (event: React.MouseEvent) => {
    const target = event.target as HTMLElement;
    const wikiLink = target.closest('.wiki-link');
    const sectionToggle = target.closest('.section-toggle');

    if (wikiLink) {
      const articleTitle = wikiLink.getAttribute('data-article');
      if (articleTitle) {
        navigate(`/article/${articleTitle}`);
      }
    } else if (sectionToggle) {
      const section = sectionToggle.closest('.wiki-section');
      const content = section?.querySelector('.wiki-section-content');
      const isExpanded = sectionToggle.getAttribute('aria-expanded') === 'true';

      if (content) {
        content.classList.toggle('collapsed');
        sectionToggle.setAttribute('aria-expanded', (!isExpanded).toString());
      }

      event.preventDefault();
      event.stopPropagation();
    }
  };

  if (loading) {
    return (
      <div className="article-page">
        <div className="article-loading">Loading article...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="article-page">
        <div className="article-error">{error}</div>
      </div>
    );
  }

  return (
    <div className="article-page">
      {heroImage ? (
        <div className="article-hero">
          <div className="hero-image-container">
            <img
              src={heroImage.url}
              alt={heroImage.description || 'Article hero image'}
              loading="eager"
            />
            <div className="hero-overlay">
              <h1 className="article-title">{decodeURIComponent(title || '')}</h1>
            </div>
          </div>
        </div>
      ) : (
        <div className="article-hero no-image">
          <h1 className="article-title">{decodeURIComponent(title || '')}</h1>
        </div>
      )}

      <article 
        className="article-content"
        onClick={handleWikiLinkClick}
        dangerouslySetInnerHTML={{ __html: content }}
      />
    </div>
  );
};

export default ArticlePage;
