const WIKIPEDIA_API_URL = 'https://en.wikipedia.org/w/api.php';

export interface SearchResult {
  title: string;
  snippet: string;
  pageid: number;
  images?: ArticleImage[];
  imagesLoading?: boolean;
}

export interface ArticleImage {
  title: string;
  url: string;
  description: string;
}

export const searchWikipedia = async (query: string): Promise<SearchResult[]> => {
  const params = new URLSearchParams({
    action: 'query',
    list: 'search',
    srsearch: query,
    format: 'json',
    origin: '*',
    srlimit: '10'
  });

  try {
    const response = await fetch(`${WIKIPEDIA_API_URL}?${params}`);
    const data = await response.json();
    const searchResults = data.query.search;

    // Return results immediately with imagesLoading state
    const initialResults = searchResults.map((result: any) => ({
      title: decodeURIComponent(result.title.replace(/_/g, ' ')),
      snippet: result.snippet,
      pageid: result.pageid,
      imagesLoading: true
    }));

    // Fetch images in parallel for all results
    const resultsWithImages = await Promise.all(
      initialResults.map(async (result: SearchResult) => {
        const images = await getArticleImages(result.title);
        return {
          ...result,
          images: images.slice(0, 4),
          imagesLoading: false
        };
      })
    );

    return resultsWithImages;
  } catch (error) {
    console.error('Error searching Wikipedia:', error);
    return [];
  }
};

export const getArticleImages = async (title: string): Promise<ArticleImage[]> => {
  const params = new URLSearchParams({
    action: 'query',
    prop: 'images|imageinfo',
    titles: title,
    imlimit: '4', // Reduced from 10 to 4 since we only show 4 images
    iiprop: 'url|extmetadata',
    format: 'json',
    origin: '*'
  });

  try {
    const response = await fetch(`${WIKIPEDIA_API_URL}?${params}`);
    const data = await response.json();
    const pages = data.query.pages;
    const pageId = Object.keys(pages)[0];
    const images = pages[pageId].images || [];

    // Filter out non-image files and get image URLs
    const imagePromises = images
      .filter((img: any) => img.title.toLowerCase().match(/\.(jpg|jpeg|png|gif)$/))
      .map(async (img: any) => {
        const imageInfoParams = new URLSearchParams({
          action: 'query',
          prop: 'imageinfo',
          titles: img.title,
          iiprop: 'url|extmetadata',
          format: 'json',
          origin: '*'
        });

        const imageResponse = await fetch(`${WIKIPEDIA_API_URL}?${imageInfoParams}`);
        const imageData = await imageResponse.json();
        const imagePages = imageData.query.pages;
        const imagePageId = Object.keys(imagePages)[0];
        const imageInfo = imagePages[imagePageId].imageinfo[0];

        return {
          title: img.title,
          url: imageInfo.url,
          description: imageInfo.extmetadata?.ImageDescription?.value || ''
        };
      });

    return Promise.all(imagePromises);
  } catch (error) {
    console.error('Error fetching article images:', error);
    return [];
  }
};

export const getArticleContent = async (title: string): Promise<string> => {
  const params = new URLSearchParams({
    action: 'parse',
    page: title,
    format: 'json',
    prop: 'text',
    origin: '*',
    disableeditsection: '1',
    mobileformat: '1'
  });

  try {
    const response = await fetch(`${WIKIPEDIA_API_URL}?${params}`);
    const data = await response.json();
    return data.parse.text['*'] || 'No content found.';
  } catch (error) {
    console.error('Error fetching article:', error);
    return 'Error loading article content.';
  }
};

export interface DidYouKnow {
  text: string;
  relatedArticles: {
    title: string;
    link: string;
  }[];
}

export const getDidYouKnowFacts = async (): Promise<DidYouKnow[]> => {
  const params = new URLSearchParams({
    action: 'parse',
    page: 'Main_Page',
    format: 'json',
    prop: 'text',
    origin: '*'
  });

  try {
    const response = await fetch(`${WIKIPEDIA_API_URL}?${params}`);
    const data = await response.json();
    const content = data.parse.text['*'];

    // Parse the HTML content
    const parser = new DOMParser();
    const doc = parser.parseFromString(content, 'text/html');

    // Find the "Did you know" section by looking for the text within any element
    const dykContainer = Array.from(doc.querySelectorAll('*')).find(
      element => element.textContent?.includes('Did you know ...')
    )?.closest('.dyk-container') || doc.querySelector('#dyk');

    if (!dykContainer) {
      console.error('Could not find Did you know container');
      return [];
    }

    // Get all list items within the container
    const dykItems = dykContainer.querySelectorAll('li');
    console.log('Found DYK items:', dykItems.length);

    const facts: DidYouKnow[] = [];

    dykItems.forEach((item, index) => {
      const text = item.textContent?.trim() || '';
      console.log(`Processing item ${index}:`, text);

      const links = Array.from(item.querySelectorAll('a'))
        .map(link => {
          const href = link.getAttribute('href');
          const title = link.textContent?.trim() || '';
          console.log(`Found link:`, { href, title });
          return {
            title: title,
            link: href?.replace('/wiki/', '') || ''
          };
        })
        .filter(link => 
          link.link && 
          !link.link.includes(':') && 
          !link.link.includes('#') &&
          link.title.length > 0
        );

      if (text && links.length > 0) {
        facts.push({
          text: `Did you know... ${text.replace(/^... that /, '')}`,
          relatedArticles: links
        });
      }
    });

    console.log('Processed facts:', facts);
    return facts;
  } catch (error) {
    console.error('Error fetching Did You Know facts:', error);
    return [];
  }
};
